import { createRouter, createWebHistory } from 'vue-router'
import HomeView from '../views/HomeView.vue'

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView
  },
  {
    path: '/productos/:tipo',
    name: 'productos',
    component: () => import('../views/ProductosView.vue'),
    props: (route) => {
      const tipo = route.params.tipo;
      const productos = {
        desayunos: {
          nombre: "Desayunos",
          descripcion: "Contamos con desayunos nutritivos y deliciosos",
          precio: 2000,
          cantidad: "Para 2 personas"
        },
        bebidas: {
          nombre: "Bebidas",
          descripcion: "Ofrecemos una variedad de bebidas refrescantes",
          precio: 1500,
          cantidad: "500 ml"
        },
        cenas: {
          nombre: "Cenas",
          descripcion: "Cenas completas y balanceadas",
          precio: 3000,
          cantidad: "Para 2 personas"
        },
        aperitivos: {
          nombre: "Aperitivos",
          descripcion: "Aperitivos perfectos para cualquier ocasión",
          precio: 1000,
          cantidad: "Porción individual"
        },
        almuerzos: {
          nombre: "Almuerzos",
          descripcion: "Almuerzos nutritivos y deliciosos",
          precio: 2500,
          cantidad: "Para 2 personas"
        }
      };
      return productos[tipo] || {};
    }
  },
  {
    path: '/contacto',
    name: 'contacto',
    component: () => import('../views/ContactoView.vue'),
    props: (route) => {
      return {
        nombre: "Chamo Olivares",
        email: "Elchamito@gmail.com"
      };
    }
  },
  {
    path: '/carrito',
    name: 'carrito',
    component: () => import('../views/CarritoView.vue')
  },
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
